mapper.select:
select 

from course_schedule
where c_id = #{cId,jdbcType=INTEGER}
  and t_id = #{tId,jdbcType=INTEGER}

mapper.delete:
delete from course_schedule
where c_id = #{cId,jdbcType=INTEGER}
  and t_id = #{tId,jdbcType=INTEGER}

mapper.insert:
insert into course_schedule (t_id, cs_time, cs_location, 
  cs_credits, cs_period, cs_num
  )
values (#{tId,jdbcType=INTEGER}, #{csTime,jdbcType=VARCHAR}, #{csLocation,jdbcType=VARCHAR}, 
  #{csCredits,jdbcType=INTEGER}, #{csPeriod,jdbcType=INTEGER}, #{csNum,jdbcType=INTEGER}
  )

mapper.update:
update course_schedule
set cs_time = #{csTime,jdbcType=VARCHAR},
  cs_location = #{csLocation,jdbcType=VARCHAR},
  cs_credits = #{csCredits,jdbcType=INTEGER},
  cs_period = #{csPeriod,jdbcType=INTEGER},
  cs_num = #{csNum,jdbcType=INTEGER}
where c_id = #{cId,jdbcType=INTEGER}
  and t_id = #{tId,jdbcType=INTEGER}
